/* Initial Loading Screen (between onboarding and main content) */
.initial-loading-screen {
  width: 100%;
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
  padding: 40px;
}

.loading-container {
  max-width: 500px;
  background: white;
  border-radius: 16px;
  padding: 60px 40px;
  box-shadow: 0 10px 40px rgba(0, 0, 0, 0.1);
  text-align: center;
}

.spinner.large {
  width: 80px;
  height: 80px;
  margin: 0 auto 40px;
  border: 6px solid #f3f3f3;
  border-top: 6px solid #667eea;
  border-right: 6px solid #764ba2;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

.loading-title {
  font-size: 1.5rem;
  color: #2d3748;
  font-weight: 600;
  margin-bottom: 32px;
  letter-spacing: -0.02em;
}

.loading-steps {
  display: flex;
  flex-direction: column;
  gap: 20px;
  margin-bottom: 32px;
  text-align: left;
}

.loading-step {
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 16px;
  background: #f8f9fa;
  border-radius: 8px;
  animation: slideIn 0.5s ease-out;
}

@keyframes slideIn {
  from {
    opacity: 0;
    transform: translateX(-20px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.step-icon {
  font-size: 1.75rem;
  flex-shrink: 0;
}

.step-text {
  color: #4a5568;
  font-size: 0.95rem;
  line-height: 1.5;
}

.loading-patience {
  font-size: 0.9rem;
  color: #a0aec0;
  font-style: italic;
  margin: 0;
}

''@")

